                                            type="number"
                                            placeholder="ID Acceso"
                                            value={newDetalleAcceso.id_acceso}
                                            onChange={(e) => setNewDetalleAcceso({ ...newDetalleAcceso, id_acceso: e.target.value })}
                                            required
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>ID Vigilante</label>
