                                            type="number"
                                            placeholder="ID Vigilante"
                                            value={newDetalleAcceso.id_vigilante}
                                            onChange={(e) => setNewDetalleAcceso({ ...newDetalleAcceso, id_vigilante: e.target.value })}
                                            required
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Fecha Salida</label>
