import React from 'react';
import { useNavigate } from 'react-router-dom';

const AdminPanel = () => {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('token'); // Eliminar el token del almacenamiento local
        navigate('/login'); // Redirigir al inicio de sesión
    };

    // Redirigir automáticamente al panel de administración
    React.useEffect(() => {
        navigate('/admin-panel'); // Redirigir al panel de administración
    }, [navigate]);

    return (
        <div>
            <h1>Redirigiendo al Panel de Administración...</h1>
            <button onClick={handleLogout}>Cerrar Sesión</button> {/* Botón de cierre de sesión */}
        </div>
    );
};

export default AdminPanel;
