import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import jwt_decode from 'jwt-decode';

const Navbar = () => {
    const token = localStorage.getItem('token');
    const [role, setRole] = useState('');

    useEffect(() => {
        if (token) {
            const decodedToken = jwt_decode(token); // Asegúrate de tener jwt-decode instalado
            setRole(decodedToken.role); // Asumiendo que el rol está en el token
        }
    }, [token]);

    return (
        <nav>
            <Link to="/">Login</Link>
            <Link to="/register">Registro</Link>
            {role === 'Admin' && <Link to="/admin">Panel de Administración</Link>}
            {role && <Link to={`/user/${role}`}>Panel de {role}</Link>}
        </nav>
    );
};

export default Navbar;
