import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            // Realiza la solicitud de inicio de sesión
            const response = await axios.post('http://localhost:3001/api/login', { email, password });
            const { token, role } = response.data; // Asegúrate de que 'role' sea el valor correcto
            localStorage.setItem('token', token);

            // Verifica el rol y redirige al panel correspondiente
            if (role === '1') {
                navigate('/admin'); // Redirigir al AdminPanel
            } else {
                navigate(`/role-panel/${role}`); // Redirigir al RolePanel
            }
        } catch (error) {
            console.error('Login failed:', error);
            alert('Error en el inicio de sesión. Verifica tus credenciales.');
        }
    };

    return (
        <div>
            <form onSubmit={handleLogin}>
                <h2>Login</h2>
                <div>
                    <label>Email:</label>
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <div>
                    <label>Contraseña:</label>
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                </div>
                <button type="submit">Iniciar Sesión</button>
            </form>
            <div>
                <p>¿No tienes una cuenta? <button onClick={() => navigate('/register')}>Registrar</button></p>
            </div>
        </div>
    );
};

export default Login;
