import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import jwtDecode from 'jwt-decode'; // Asegúrate de que esta línea sea correcta

const Navbar = () => {
    const token = localStorage.getItem('token');
    const [role, setRole] = useState('');

    useEffect(() => {
        if (token) {
            try {
                const decodedToken = jwtDecode(token); // Asegúrate de que esta línea sea correcta
                setRole(decodedToken.role); // Asumiendo que el rol está en el token
            } catch (error) {
                console.error('Error al decodificar el token:', error);
            }
        }
    }, [token]);

    return (
        <nav>
            <Link to="/">Login</Link>
            <Link to="/register">Registro</Link>
            {role === '1' && <Link to="/admin">Panel de Administración</Link>}
            {role && <Link to={`/user/${role}`}>Panel de {role}</Link>}
        </nav>
    );
};

export default Navbar;
