                                                    ) : (
                                                        <>
                                                            <button onClick={() => setEditSensor(sensor)} className="btn-action">Editar</button>
                                                            <button onClick={() => handleDeleteSensor(sensor.id_sensor)} className="btn-action">Eliminar</button>
                                                        </>
                                                    )}
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default AdminPanel;
