import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Register = () => {
    const [nombre, setNombre] = useState('');
    const [telefono, setTelefono] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [id_rol, setIdRol] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Verificar si el usuario ya está autenticado
        const token = localStorage.getItem('token');
        if (token) {
            // Si hay un token, redirigir al panel de administración
            navigate('/admin-panel');
        }
    }, [navigate]);

    const handleRegister = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:3001/api/register', { nombre, telefono, email, password, id_rol });
            alert('Usuario registrado con éxito. Redirigiendo al inicio de sesión...');
            console.log('Registro exitoso, redirigiendo al login...');
            navigate('/login'); // Redirigir al inicio de sesión
        } catch (error) {
            console.error('Error al registrar el usuario:', error);
            alert('Error al registrar el usuario. Verifica los datos.');
        }
    };

    return (
        <form onSubmit={handleRegister}>
            <h2>Registro</h2>
            <div>
                <label>Nombre:</label>
                <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
            </div>
            <div>
                <label>Teléfono:</label>
                <input type="text" value={telefono} onChange={(e) => setTelefono(e.target.value)} required />
            </div>
            <div>
                <label>Email:</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
            </div>
            <div>
                <label>Contraseña:</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
            </div>
            <div>
                <label>Rol:</label>
                <select value={id_rol} onChange={(e) => setIdRol(e.target.value)} required>
                    <option value="">Selecciona un rol</option>
                    <option value="2">Residente</option>
                    <option value="3">Mantenimiento</option>
                    <option value="4">Vigilante</option>
                    <option value="5">Visitante</option>
                </select>
            </div>
            <button type="submit">Registrar</button>
        </form>
    );
};

export default Register;
