import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import * as XLSX from 'xlsx';
import './AdminPanel.css';

const AdminPanel = () => {
    const navigate = useNavigate();
    const [activeSection, setActiveSection] = useState('users'); // Controla la sección activa
    const [users, setUsers] = useState([]); // Lista de usuarios
    const [newUser, setNewUser] = useState({ nombre: '', telefono: '', email: '', password: '', id_rol: '' }); // Formulario nuevo usuario
    const [editUser, setEditUser] = useState(null); // Usuario en edición
    const [excelFile, setExcelFile] = useState(null); // Archivo Excel subido

    // Obtener todos los usuarios al cargar el componente (solo para la sección de usuarios)
    useEffect(() => {
        if (activeSection === 'users') {
            fetchUsers();
        }
    }, [activeSection]);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/usuarios', {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setUsers(response.data);
        } catch (error) {
            console.error('Error al obtener usuarios:', error);
            alert('Error al cargar los usuarios');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('roleName');
        navigate('/', { replace: true });
    };

    // Insertar nuevo usuario
    const handleInsertUser = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:3001/api/register', newUser, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            alert('Usuario registrado con éxito');
            setNewUser({ nombre: '', telefono: '', email: '', password: '', id_rol: '' });
            fetchUsers();
        } catch (error) {
            console.error('Error al registrar usuario:', error);
            alert('Error al registrar el usuario');
        }
    };

    // Modificar usuario
    const handleUpdateUser = async (id) => {
        try {
            await axios.put(`http://localhost:3001/api/usuarios/${id}`, editUser, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            alert('Usuario actualizado con éxito');
            setEditUser(null);
            fetchUsers();
        } catch (error) {
            console.error('Error al actualizar usuario:', error);
            alert('Error al actualizar el usuario');
        }
    };

    // Eliminar usuario
    const handleDeleteUser = async (id) => {
        if (window.confirm('¿Estás seguro de eliminar este usuario?')) {
            try {
                await axios.delete(`http://localhost:3001/api/usuarios/${id}`, {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
                });
                alert('Usuario eliminado con éxito');
                fetchUsers();
            } catch (error) {
                console.error('Error al eliminar usuario:', error);
                alert('Error al eliminar el usuario');
            }
        }
    };

    // Importar desde Excel
    const handleExcelUpload = async (e) => {
        e.preventDefault();
        if (!excelFile) {
            alert('Por favor selecciona un archivo Excel');
            return;
        }

        const reader = new FileReader();
        reader.onload = async (event) => {
            const data = new Uint8Array(event.target.result);
            const workbook = XLSX.read(data, { type: 'array' });
            const sheet = workbook.Sheets[workbook.SheetNames[0]];
            const jsonData = XLSX.utils.sheet_to_json(sheet);

            try {
                const response = await axios.post('http://localhost:3001/api/import-users', jsonData, {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
                });
                alert(response.data.message);
                fetchUsers();
            } catch (error) {
                console.error('Error al importar usuarios:', error);
                alert('Error al importar usuarios desde Excel');
            }
        };
        reader.readAsArrayBuffer(excelFile);
    };

    return (
        <div className="admin-panel-container">
            {/* Barra de navegación superior con todas las tablas */}
            <nav className="admin-nav">
                <ul>
                    <li>
                        <button onClick={() => setActiveSection('users')} className={activeSection === 'users' ? 'active' : ''}>
                            Administración de Usuarios
                        </button>
                    </li>
                    <li>
                        <button onClick={() => setActiveSection('vigilantes')} className={activeSection === 'vigilantes' ? 'active' : ''}>
                            Administración de Vigilantes
                        </button>
                    </li>
                    <li>
                        <button onClick={() => setActiveSection('autos')} className={activeSection === 'autos' ? 'active' : ''}>
                            Administración de Autos
                        </button>
                    </li>
                    <li>
                        <button onClick={() => setActiveSection('accesos')} className={activeSection === 'accesos' ? 'active' : ''}>
                            Gestión de Accesos
                        </button>
                    </li>
                    <li>
                        <button onClick={() => setActiveSection('detalle_acceso')} className={activeSection === 'detalle_acceso' ? 'active' : ''}>
                            Registro de Entradas/Salidas
                        </button>
                    </li>
                    <li>
                        <button onClick={() => setActiveSection('sensores')} className={activeSection === 'sensores' ? 'active' : ''}>
                            Control de Sensores
                        </button>
                    </li>
                    <li>
                        <button onClick={handleLogout} className="logout-button">Cerrar Sesión</button>
                    </li>
                </ul>
            </nav>

            <div className="admin-panel-box">
                {activeSection === 'users' && (
                    <>
                        <h1>Administración de Usuarios</h1>

                        {/* Formulario para insertar nuevo usuario */}
                        <form onSubmit={handleInsertUser} className="insert-user-form">
                            <div className="form-row">
                                <input
                                    type="text"
                                    placeholder="Nombre"
                                    value={newUser.nombre}
                                    onChange={(e) => setNewUser({ ...newUser, nombre: e.target.value })}
                                    required
                                />
                                <input
                                    type="text"
                                    placeholder="Teléfono"
                                    value={newUser.telefono}
                                    onChange={(e) => setNewUser({ ...newUser, telefono: e.target.value })}
                                    required
                                />
                                <input
                                    type="email"
                                    placeholder="Email"
                                    value={newUser.email}
                                    onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                                    required
                                />
                                <input
                                    type="password"
                                    placeholder="Contraseña"
                                    value={newUser.password}
                                    onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
                                    required
                                />
                                <select
                                    value={newUser.id_rol}
                                    onChange={(e) => setNewUser({ ...newUser, id_rol: e.target.value })}
                                    required
                                >
                                    <option value="">Selecciona un rol</option>
                                    <option value="1">Admin</option>
                                    <option value="2">Residente</option>
                                    <option value="3">Mantenimiento</option>
                                    <option value="4">Vigilante</option>
                                    <option value="5">Visitante</option>
                                </select>
                                <button type="submit">Agregar Usuario</button>
                            </div>
                        </form>

                        {/* Formulario para importar desde Excel */}
                        <div className="excel-import-section">
                            <form onSubmit={handleExcelUpload}>
                                <div className="form-row">
                                    <label>Importar desde Excel: </label>
                                    <input
                                        type="file"
                                        accept=".xlsx, .xls"
                                        onChange={(e) => setExcelFile(e.target.files[0])}
                                    />
                                    <button type="submit">Importar</button>
                                </div>
                            </form>
                            <p>El archivo debe tener columnas: nombre, telefono, email, password, id_rol</p>
                        </div>

                        {/* Tabla de usuarios */}
                        <table className="users-table">
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Nombre</th>
                                    <th>Teléfono</th>
                                    <th>Email</th>
                                    <th>Rol</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map((user) => (
                                    <tr key={user.id_usuario}>
                                        <td>{user.id_usuario}</td>
                                        <td>
                                            {editUser && editUser.id_usuario === user.id_usuario ? (
                                                <input
                                                    type="text"
                                                    value={editUser.nombre}
                                                    onChange={(e) => setEditUser({ ...editUser, nombre: e.target.value })}
                                                />
                                            ) : (
                                                user.nombre
                                            )}
                                        </td>
                                        <td>
                                            {editUser && editUser.id_usuario === user.id_usuario ? (
                                                <input
                                                    type="text"
                                                    value={editUser.telefono}
                                                    onChange={(e) => setEditUser({ ...editUser, telefono: e.target.value })}
                                                />
                                            ) : (
                                                user.telefono
                                            )}
                                        </td>
                                        <td>
                                            {editUser && editUser.id_usuario === user.id_usuario ? (
                                                <input
                                                    type="email"
                                                    value={editUser.email}
                                                    onChange={(e) => setEditUser({ ...editUser, email: e.target.value })}
                                                />
                                            ) : (
                                                user.email
                                            )}
                                        </td>
                                        <td>
                                            {editUser && editUser.id_usuario === user.id_usuario ? (
                                                <select
                                                    value={editUser.id_rol}
                                                    onChange={(e) => setEditUser({ ...editUser, id_rol: e.target.value })}
                                                >
                                                    <option value="1">Admin</option>
                                                    <option value="2">Residente</option>
                                                    <option value="3">Mantenimiento</option>
                                                    <option value="4">Vigilante</option>
                                                    <option value="5">Visitante</option>
                                                </select>
                                            ) : (
                                                user.id_rol
                                            )}
                                        </td>
                                        <td>
                                            {editUser && editUser.id_usuario === user.id_usuario ? (
                                                <>
                                                    <button onClick={() => handleUpdateUser(user.id_usuario)}>Guardar</button>
                                                    <button onClick={() => setEditUser(null)}>Cancelar</button>
                                                </>
                                            ) : (
                                                <>
                                                    <button onClick={() => setEditUser(user)}>Editar</button>
                                                    <button onClick={() => handleDeleteUser(user.id_usuario)}>Eliminar</button>
                                                </>
                                            )}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </>
                )}

                {activeSection === 'vigilantes' && (
                    <div>
                        <h1>Administración de Vigilantes</h1>
                        <p>Placeholder: Aquí puedes agregar un formulario y tabla para gestionar vigilantes (nombre, teléfono, email, turno).</p>
                    </div>
                )}

                {activeSection === 'autos' && (
                    <div>
                        <h1>Administración de Autos</h1>
                        <p>Placeholder: Aquí puedes agregar un formulario y tabla para gestionar autos (placa, modelo, color, id_usuario).</p>
                    </div>
                )}

                {activeSection === 'accesos' && (
                    <div>
                        <h1>Gestión de Accesos</h1>
                        <p>Placeholder: Aquí puedes agregar un formulario y tabla para gestionar accesos (código, estado, fechas, id_usuario, id_auto).</p>
                    </div>
                )}

                {activeSection === 'detalle_acceso' && (
                    <div>
                        <h1>Registro de Entradas/Salidas</h1>
                        <p>Placeholder: Aquí puedes agregar un formulario y tabla para registrar entradas/salidas (id_acceso, id_vigilante, fechas).</p>
                    </div>
                )}

                {activeSection === 'sensores' && (
                    <div>
                        <h1>Control de Sensores</h1>
                        <p>Placeholder: Aquí puedes agregar un formulario y tabla para gestionar sensores (ubicación, estado).</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default AdminPanel;
