import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const AdminPanel = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const role = location.state?.role; // Obtén el rol desde el estado de la ubicación

    const handleLogout = () => {
        localStorage.removeItem('token'); // Eliminar el token del almacenamiento local
        navigate('/login'); // Redirigir al inicio de sesión
    };

    // Mensaje de bienvenida según el rol
    const getWelcomeMessage = () => {
        switch (role) {
            case '2':
                return 'Bienvenido al panel de Residentes';
            case '3':
                return 'Bienvenido al panel de Mantenimiento';
            case '4':
                return 'Bienvenido al panel de Vigilantes';
            case '5':
                return 'Bienvenido al panel de Visitantes';
            default:
                return 'Bienvenido al Panel de Administración';
        }
    };

    return (
        <div>
            <h1>{getWelcomeMessage()}</h1>
            {/* Aquí puedes agregar contenido específico según el rol */}
            {role === '2' && <p>Contenido específico para Residentes</p>}
            {role === '3' && <p>Contenido específico para Mantenimiento</p>}
            {role === '4' && <p>Contenido específico para Vigilantes</p>}
            {role === '5' && <p>Contenido específico para Visitantes</p>}
            <button onClick={handleLogout}>Cerrar Sesión</button> {/* Botón de cierre de sesión */}
        </div>
    );
};

export default AdminPanel;
